#include<cstdio>#include<iostream>#include<ctime>#include<cstdlib>using namespace std;const int dian=8;int n,m,T,u,v,l;bool flag[2501][2501];int main(){	freopen("testdata.in","w",stdout);	srand(time(0));	n=rand()%dian;	while(n==0||n==1) n=rand()%dian;T=rand()%1000;	m=rand()%(n*(n-1)/2)+n;	if(m>n*(n-1)/2) m=n*(n-1)/2;	cout<<n<<" "<<m<<" "<<T<<endl;	while(m--){		u=rand()%n+1;v=rand()%n+1;l=rand()%100;		while(u==v||flag[u][v]) {u=rand()%n+1;v=rand()%n+1;}		flag[u][v]=flag[v][u]=true;		cout<<u<<" "<<v<<" "<<l<<endl;	}	fclose(stdout);	return 0;}